/**
 * Fix shortcut. Mac use Command, others use Ctrl.
 */
function fixShortcut(name) {
  if (isMac) {
    name = name.replace('Ctrl', 'Cmd');
  } else {
    name = name.replace('Cmd', 'Ctrl');
  }
  return name;
}


/**
 * Create icon element for toolbar.
 */
function createIcon(name, options) {
  options = options || {};
  var el = document.createElement('li');
  var link = document.createElement('a');

  if (options.href) {
    $(link).attr({
      href: options.href,
      target: "_blank"
    });
  }

  link.title = options.title;

  var shortcut = options.shortcut || shortcuts[name];
  if (shortcut) {
    shortcut = fixShortcut(shortcut);
    link.title += shortcut;
    link.title = link.title.replace('Cmd', '⌘');
    if (isMac) {
      link.title = link.title.replace('Alt', '⌥');
    }
  }

  link.className = "meditor-icon-" + name;
  link.innerHTML = "<span class='fa fa-" + options.icon + "'></span>";
  el.appendChild(link);

  if (name === 'image') {
    var imageMenu = document.createElement('div');
    imageMenu.className = "toolbar-menu toolbar-menu-image";
    imageMenu.innerHTML = "<ul>"
      + "<li><a class='menu-item menu-item-upload-image' href='javascript:;' title='上传图片'><span>上传图片</span></a></li>"
      + "<li><a class='menu-item menu-item-external-image' href='javascript:;' title='外链图片'><span>外链图片</span></a></li>"
      + "</ul>"
      + "<input type='file' class='input-upload-image' title='上传图片' accept='image/*'>";

    el.appendChild(imageMenu);
  }

  return el;
}

