/**
 * Interface of Editor.
 */
function Editor(options) {
  options = options || {};

  if (options.element) {
    this.element = options.element;
  }

  options.toolbar = options.toolbar || Editor.toolbar;
  // you can customize toolbar with object
  // [{name: 'bold', shortcut: 'Ctrl-B', className: 'icon-bold'}]

  if (!options.hasOwnProperty('status')) {
    options.status = ['lines', 'words', 'cursor'];
  }

  this.options = options;

  // If user has passed an element, it should auto rendered
  if (this.element) {
    this.render();
  }
}

/**
 * Default toolbar elements.
 */
Editor.toolbar = toolbar;

/**
 * Default markdown render.
 */
Editor.markdown = function (text) {
  if (window.marked) {
    // use marked as markdown parser
    return marked(text);
  }
};

/**
 * Render editor to the given element.
 */
Editor.prototype.render = function (el) {
  if (!el) {
    el = this.element || document.getElementsByTagName('textarea')[0];
  }

  if (this._rendered && this._rendered === el) {
    // Already rendered.
    return;
  }

  this.element = el;
  var options = this.options;

  var self = this;
  var keyMaps = {};

  for (var key in shortcuts) {
    (function (key) {
      keyMaps[fixShortcut(key)] = function (cm) {
        shortcuts[key](self);
      };
    })(key);
  }

  keyMaps["Enter"] = "newlineAndIndentContinueMarkdownList";
  keyMaps['Tab'] = 'tabAndIndentContinueMarkdownList';
  keyMaps['Shift-Tab'] = 'shiftTabAndIndentContinueMarkdownList';

  this.codemirror = CodeMirror.fromTextArea(el, {
    mode: 'markdown',
    theme: 'paper',
    tabSize: '2',
    indentWithTabs: true,
    lineNumbers: false,
    autofocus: true,
    extraKeys: keyMaps
  });

  if (options.toolbar !== false) {
    this.createToolbar();
  }
  if (options.status !== false) {
    this.createStatusbar();
  }

  this._rendered = this.element;
};

Editor.prototype.createToolbar = function (items) {
  items = items || this.options.toolbar;

  if (!items || items.length === 0) {
    return;
  }

  var bar = document.createElement('div');
  bar.className = 'editor-toolbar';

  var ul = document.createElement('ul');

  var self = this;

  var el;
  self.toolbar = {};

  for (var i = 0; i < items.length; i++) {
    (function (item) {
      var el;
      if (item.name) {
        el = createIcon(item.name, item);
      } else if (item === '|') {
        el = createSep();
      } else {
        el = createIcon(item);
      }

      // bind events, special for info
      if (item.action) {
        el.onclick = function (e) {
          item.action(self);
        };
      }

      if (item.name === 'image') {
        // Toggle menu
        $(el).click(function (e) {
          $(this).toggleClass('menu-on');
          e.stopPropagation();
        });

        $(document).click(function () {
          $(el).removeClass('menu-on');
        });

        // Init uploader
        self.uploader = simple.uploader(self.options.upload);

        // Upload success
        self.uploader.on('uploadsuccess', function (e, file, result) {
          drawImage(self, result.file_path);
        });

        // Open file select dialog
        $(el).find('.menu-item-upload-image').on('click', function (e) {
          $(el).find('.input-upload-image').click();
        });

        // Upload file
        $(el).find('.input-upload-image').on('change', function () {
          self.uploader.upload(this.files);
        });

        // Insert external image
        $(el).find('.menu-item-external-image').click(function () {
          drawImage(self);
        });
      }

      self.toolbar[item.name || item] = el;
      ul.appendChild(el);
    })(items[i]);
  }

  bar.appendChild(ul);

  var cm = this.codemirror;
  cm.on('cursorActivity', function () {
    var stat = getState(cm);

    for (var key in self.toolbar) {
      (function (key) {
        var el = self.toolbar[key];
        if (stat[key]) {
          el.className += ' active';
        } else {
          el.className = el.className.replace(/\s*active\s*/g, '');
        }
      })(key);
    }
  });

  var cmWrapper = cm.getWrapperElement();
  var $wapper = $("<div class='meditor'></div>");

  cmWrapper.parentNode.insertBefore(bar, cmWrapper);
  $wapper.insertBefore(bar);
  $wapper.append(bar);
  $wapper.append(cmWrapper);
  this.el = $wapper;
  return bar;
};

Editor.prototype.createStatusbar = function (status) {
  status = status || this.options.status;

  if (!status || status.length === 0) return;

  var bar = document.createElement('div');
  bar.className = 'editor-statusbar';

  var pos, cm = this.codemirror;
  for (var i = 0; i < status.length; i++) {
    (function (name) {
      var el = document.createElement('span');
      el.className = name;
      if (name === 'words') {
        el.innerHTML = '0';
        cm.on('update', function () {
          el.innerHTML = wordCount(cm.getValue());
        });
      } else if (name === 'lines') {
        el.innerHTML = '0';
        cm.on('update', function () {
          el.innerHTML = cm.lineCount();
        });
      } else if (name === 'cursor') {
        el.innerHTML = '0:0';
        cm.on('cursorActivity', function () {
          pos = cm.getCursor();
          el.innerHTML = pos.line + ':' + pos.ch;
        });
      }
      bar.appendChild(el);
    })(status[i]);
  }
  var cmWrapper = this.codemirror.getWrapperElement();
  cmWrapper.parentNode.insertBefore(bar, cmWrapper.nextSibling);
  return bar;
};

/**
 * Get or set the text content.
 */
Editor.prototype.value = function (val) {
  if (val) {
    this.codemirror.getDoc().setValue(val);
    return this;
  } else {
    return this.codemirror.getValue();
  }
};


