XEditor.prototype.setValue = function (content) {
  if (this.rich) {
    this.richEditor.setValue(content);
  } else {
    this.markdownEditor.codemirror.setValue(content);
  }
};

XEditor.prototype.sync = function () {
  this.textarea.text(this.getValue());
};
